@keyframes indeterminate {
    0% {
      left: -100%;
    }
    100% {
      left: 100%;
    }
  }
  
  @keyframes query {
    0% {
      right: -100%;
    }
    100% {
      right: 100%;
    }
  }

  @keyframes bufferWave {
    0% {
      left: -100%;
      width: 40%;
    }
    50% {
      left: 50%;
      width: 60%;
    }
    100% {
      left: 100%;
      width: 40%;
    }
  }
  
  .animate-indeterminate {
    position: absolute;
    left: -100%;
    width: 40%;
    animation: indeterminate 1.5s infinite linear;
  }
  
  .animate-query {
    position: absolute;
    right: -100%;
    width: 40%;
    animation: query 1.5s infinite linear;
  }

  .buffer-wave {
    position: absolute;
    left: -100%;
    height: 100%;
    width: 40%;
    background: linear-gradient(to right, rgba(0, 0, 0, 0.1), rgba(255, 255, 255, 0.3));
    animation: bufferWave 2s infinite ease-in-out;
  }

